    private void PertambahanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_PertambahanActionPerformed
        int Angka1, Angka2, Hasil=0;
        Angka1 = Integer.parseInt(angka1.getText());
        Angka2 = Integer.parseInt(angka2.getText());
        
        Hasil = Angka1 + Angka2;
        
        if(penjabaran.isSelected()){
            hasil.setText("Hasil Penjumlahan " +String.valueOf(Angka1)+ " + " +String.valueOf(Angka2)+ " adalah " 
            +String.valueOf(Hasil));
        }else{
            hasil.setText(String.valueOf(Hasil));
        }
        
    }//GEN-LAST:event_PertambahanActionPerformed

