    private void PenguranganActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_PenguranganActionPerformed
        int Angka1, Angka2, Hasil=0;
        Angka1 = Integer.parseInt(angka1.getText());
        Angka2 = Integer.parseInt(angka2.getText());
        
        Hasil = Angka1 - Angka2;
        
        if(penjabaran.isSelected()){
             hasil.setText("Hasil Pengurangan " +String.valueOf(Angka1)+ " - " +String.valueOf(Angka2)+ " adalah " 
             +String.valueOf(Hasil));
        }else{
            hasil.setText(String.valueOf(Hasil));
        }
    }//GEN-LAST:event_PenguranganActionPerformed

    private void PerkalianActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_PerkalianActionPerformed
        int Angka1, Angka2, Hasil=0;
        Angka1 = Integer.parseInt(angka1.getText());
        Angka2 = Integer.parseInt(angka2.getText());
        
        Hasil = Angka1 * Angka2;
        
        if(penjabaran.isSelected()){
            hasil.setText("Hasil Perkalian " +String.valueOf(Angka1)+ " X " +String.valueOf(Angka2)+ " adalah " 
            +String.valueOf(Hasil));
        }else{
            hasil.setText(String.valueOf(Hasil));
        }
    }//GEN-LAST:event_PerkalianActionPerformed

    private void PembagianActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_PembagianActionPerformed
        int Angka1, Angka2, Hasil=0;
        Angka1 = Integer.parseInt(angka1.getText());
        Angka2 = Integer.parseInt(angka2.getText());
        
        Hasil = Angka1 / Angka2;
        
        if(penjabaran.isSelected()){
           hasil.setText("Hasil Pembagian " +String.valueOf(Angka1)+ " : " +String.valueOf(Angka2)+ 
           " adalah " +String.valueOf(Hasil)); 
        }else{
           hasil.setText(String.valueOf(Hasil)); 
        }
            
    }//GEN-LAST:event_PembagianActionPerformed

