    private void comboInputActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_comboInputActionPerformed
        int angka1, angka2, hasil;
        String option = (String)comboInput.getSelectedItem();
        angka1 = Integer.parseInt(txt1.getText());
        angka2 = Integer.parseInt(txt2.getText());
        
        switch(option)
        {
            case "Pertambahan":
                hasil = angka1 + angka2;
                jLabel4.setText(String.valueOf(hasil));
                break;
            case "Pengurangan":
                hasil = angka1 - angka2;
                jLabel4.setText(String.valueOf(hasil));
                break;
            case "Perkalian":
                hasil = angka1 * angka2;
                jLabel4.setText(String.valueOf(hasil));
                break;
            case "Pembagian":
                hasil = angka1 / angka2;
                jLabel4.setText(String.valueOf(hasil));
        }
    }//GEN-LAST:event_comboInputActionPerformed


