    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String pilihan = (String) roomtype.getSelectedItem();
        int tarif,totalharga,superior,regular,firstClass,executive,hari;
        tarif = 0;
        totalharga= 0;
        hari = (Integer)lamaNginap.getValue();
        superior=2000000;
        regular=1000000;
        firstClass=500000;
        executive=400000;
        
        switch(pilihan){
            case "Superior":
                tarif = superior;
                totalharga= hari * superior;
                break;
            case "Regular":
                tarif = regular;
                totalharga = hari * regular;
                break;
            case "First Class":
                tarif = firstClass;
                totalharga = hari * firstClass;
                break;
            case "Executive":
                tarif = executive;
                totalharga = hari * executive;
                break;
        }
        
        if (cbHarian.isSelected()== true){
            lblHasil.setText("<html><div style='text-align: center;'>"
            + "Harga per Malam : Rp " + String.valueOf(tarif) + ",-" + "<br>"
            + "Total harga : Rp " + String.valueOf(totalharga) + ",-</div></html>");
        }else {
            lblHasil.setText("<html><div style='text-align: center;'></div></html>"
            + "Total Harga: Rp " + String.valueOf(totalharga) + "-,</div></html>"
            );
        }
    }//GEN-LAST:event_jButton1ActionPerformed

